const UserList: React.FC<IUserListDashBoard> = ({
  getUsers,
  deleteUser,
  auth,
  error,
  logout,
  clearErrors,
  users,
  history,
}) => {
  const classes = useStyles();
  const [searched, setSearched] = React.useState("");
  const [open, setOpen] = React.useState(false);
  // const { users } = useSelector((state) => state);
  const [data, setdata] = React.useState<IUser[]>([]);
  // const [selectedUser, setselectedUser] = React.useState();

  // let history = useHistory();

  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };

  useEffect(() => {
    getUsers();
  }, []);

  useEffect(() => {
    setdata(users);
  }, [users]);

  const requestSearch = (searchedVal: string) => {
    if (searchedVal !== "") {
      const filteredRows = users.filter((row) => {
        return (
          row.email.toLowerCase().includes(searchedVal.toLowerCase()) ||
          row.mobileNumber?.includes(searchedVal.toLowerCase()) ||
          row.firstName?.toLowerCase().includes(searchedVal.toLowerCase()) ||
          row.lastName?.toLowerCase().includes(searchedVal.toLowerCase())
        );
      });
      setdata(filteredRows);
    } else {
      setdata(users);
    }
  };

  const cancelSearch = () => {
    setSearched("");
    requestSearch("searched");
    setdata(users);
  };

  // const signOut = () => {
  // 	store.dispatch(isLogged(false));
  // 	history.push("/login");
  // };

  // const ViewUser = (person) => {
  // 	setselectedUser(person);
  // 	console.log(switcher);
  // 	setswitcher(true);
  // };

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        position="absolute"
        className={clsx(classes.appBar, open && classes.appBarShift)}
      >
        <Toolbar className={classes.toolbar}>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            className={clsx(
              classes.menuButton,
              open && classes.menuButtonHidden
            )}
          >
            <MenuIcon />
          </IconButton>
          <Typography
            component="h1"
            variant="h6"
            color="inherit"
            noWrap
            className={classes.title}
          >
            Dashboard
          </Typography>
          <Tooltip title="Sign out" aria-label="add">
            <Link to="/">
              <IconButton color="inherit" onClick={logout}>
                <Badge color="secondary">
                  <ExitToAppIcon style={{ color: "white" }} />
                </Badge>
              </IconButton>
            </Link>
          </Tooltip>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        classes={{
          paper: clsx(classes.drawerPaper, !open && classes.drawerPaperClose),
        }}
        open={open}
      >
        <div className={classes.toolbarIcon}>
          <IconButton onClick={handleDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <List>{}</List>
        <Divider />
        <List>{}</List>
      </Drawer>
      <main className={classes.content}>
        <div className={classes.appBarSpacer} />
        <Container maxWidth="lg" className={classes.container}>
          <Grid container spacing={3}>
            {/* Chart */}
            <Grid item xs={12} md={8} lg={9}></Grid>
            {/* Recent Deposits */}
            <Grid item xs={12} md={4} lg={3}>
              <Paper></Paper>
            </Grid>
            {/* Recent Orders */}
            <Grid item xs={12}>
              (
              <Paper className={classes.paper}>
                <SearchBar
                  value={searched}
                  onChange={(searchVal) => requestSearch(searchVal)}
                  onCancelSearch={() => cancelSearch()}
                />
                <React.Fragment>
                  <Table size="small">
                    <TableHead>
                      <TableRow>
                        <TableCell>Profile</TableCell>
                        <TableCell>Name</TableCell>
                        <TableCell>Email</TableCell>
                        <TableCell>Address</TableCell>
                        <TableCell>Phone</TableCell>
                        <TableCell align="right">Action</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {data.map((user, id) => (
                        <TableRow key={user.email + "_" + id}>
                          <TableCell>
                            {user.userImage && (
                              <img
                                width="40%"
                                className={classes.media}
                                src={user.userImage}
                                alt=""
                              />
                            )}
                          </TableCell>
                          <TableCell>
                            {user?.firstName + " " + user?.lastName}
                          </TableCell>
                          <TableCell>{user.email}</TableCell>
                          <TableCell>{user.Address}</TableCell>
                          <TableCell>{user.mobileNumber}</TableCell>
                          <TableCell align="right">
                            <Button
                              variant="contained"
                              onClick={() =>
                                history.push(`/userDashboard?_id=${user._id}`)
                              }
                              color="primary"
                            >
                              View
                            </Button>
                            <Button
                              variant="contained"
                              onClick={() => user._id && deleteUser(user._id)}
                              color="secondary"
                            >
                              Delete
                            </Button>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                  <div className={classes.seeMore}></div>
                </React.Fragment>
              </Paper>
              )
            </Grid>
          </Grid>
        </Container>
      </main>
    </div>
  );
};

const mapStateToProps = (state: IRootState) => ({
  auth: state.auth,
  error: state.error,
  users: state.userList.users,
});

export default connect(mapStateToProps, {
  getUsers,
  deleteUser,
  logout,
  clearErrors,
})(UserList);
