import {
    LOGIN_SUCCESS,
    LOGOUT,
  } from '../actions/types';

  import { IUserRedux, actionTypes, IUser } from "../../utils/interfaces";
  interface IAction {
    type: actionTypes;
    payload: IUser;
  }

  const initialState:IUserRedux = {
    isAuth: false,
    isLoading: false,
    user: null,
  };

  const userReducer= (state:IUserRedux = initialState, action: IAction) =>{
    switch (action.type) {
      case LOGIN_SUCCESS:
        return {
          user: { ...action.payload },
          isAuth: true,
          isLoading: false
        };
      case LOGOUT:
        return {
          ...initialState
        };
        case REGISTER_SUCCESS:
          localStorage.setItem('token', action.payload.token||"");
          return {
            ...state,
            ...action.payload,
            isAuthenticated: true,
            isLoading: false
          };
      default:
        return state;
    }
  }


  export default userReducer
